    private void btn_addActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_addActionPerformed
        try {
            int value = Integer.parseInt(input_add.getText()); // Pegando o valor do campo input_add
            fila.add(value); // adicionado novo item na fila
            String text = value + " foi adicionado com sucesso na fila. \n"; // mensagem para ser exibida no console
            ta_saida.setText(ta_saida.getText() + text); // adicionado mensagem no console.
        } catch (ArrayIndexOutOfBoundsException e) {
            JOptionPane.showMessageDialog(rootPane, "A Fila está cheia, tente aumentar o seu tamanho.", "Alerta", JOptionPane.INFORMATION_MESSAGE);
        } catch (Exception e) {
            JOptionPane.showMessageDialog(rootPane, "Digite apenas valores numéricos inteiros!", "Alerta", JOptionPane.INFORMATION_MESSAGE);
        } finally {
            input_add.setText(""); // Limpando o campo input_add
        }
    }//GEN-LAST:event_btn_addActionPerformed

    private void btn_SetcapacidadeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_SetcapacidadeActionPerformed
        try {
            int value = Integer.parseInt(input_capacidade.getText());
            int oldCapacity = fila.getCapacity(); // Pegando o tamanho antigo da fila
            fila.resize(value); // Alterando tamanho da fila
            String text = "O tamanho da fila fou alterado de " + oldCapacity + " para " + value + ".\n"; // String que será impressa nos logs
            ta_saida.setText(ta_saida.getText() + text); // Imprimindo mensagem
        } catch (Exception e) {
            JOptionPane.showMessageDialog(rootPane, "Digite apenas valores numéricos inteiros maiores que o tamanho da fila!", "Alerta", JOptionPane.INFORMATION_MESSAGE);
        } finally {
            input_capacidade.setText(""); // Limpando o campo input_capacidade
        } 
    }//GEN-LAST:event_btn_SetcapacidadeActionPerformed

