    private void btn_getremoveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_getremoveActionPerformed
        try {
            fila.remove(); // removendo elemento da Fila
            String text = "O Primeiro elemento da fila foi removido \n";// String que será impressa nos logs
            ta_saida.setText(ta_saida.getText() + text); // Imprimindo mensagem
        } catch (IllegalStateException e) {
            JOptionPane.showMessageDialog(rootPane, "A fial está vazia!", "Alerta", JOptionPane.INFORMATION_MESSAGE);
        } catch (Exception e) {
            JOptionPane.showMessageDialog(rootPane, "Ops! Ocorreu um erro desconhecido.", "Alerta", JOptionPane.INFORMATION_MESSAGE);
        }
    }//GEN-LAST:event_btn_getremoveActionPerformed

    private void btn_getStatusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_getStatusActionPerformed
        try {
            String text;
            if(fila.isEmpty()) { 
                text = "A fila está vazia | ";
            } else {
                text = "A fina não está vazia | ";
            }
            
            if(fila.isFull()) {
                text += "A fila está cheia.\n";
            } else {
                text += "A fila não está cheia.\n";
            }
            ta_saida.setText(ta_saida.getText() + text); // Imprimindo mensagem
        } catch (Exception e) {
            JOptionPane.showMessageDialog(rootPane, "Ops! Ocorreu um erro desconhecido.", "Alerta", JOptionPane.INFORMATION_MESSAGE);
        }
    }//GEN-LAST:event_btn_getStatusActionPerformed

    private void btn_getTamanhoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_getTamanhoActionPerformed
        try {
            String text = "O tamanho da fila é de " + fila.size() + ".\n";
            ta_saida.setText(ta_saida.getText() + text); // Imprimindo mensagem
        } catch (Exception e) {
            JOptionPane.showMessageDialog(rootPane, "Ops! Ocorreu um erro desconhecido.", "Alerta", JOptionPane.INFORMATION_MESSAGE);
        }
    }//GEN-LAST:event_btn_getTamanhoActionPerformed

    private void btn_getCapacidadeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_getCapacidadeActionPerformed
        try {
            String text = "A capacidade da fila é de " + fila.getCapacity() + ".\n";
            ta_saida.setText(ta_saida.getText() + text); // Imprimindo mensagem
            input_capacidade.setText(""); // Limpando o campo input_capacidade
        } catch (Exception e) {
            JOptionPane.showMessageDialog(rootPane, "Ops! Ocorreu um erro desconhecido.", "Alerta", JOptionPane.INFORMATION_MESSAGE);
        }
    }//GEN-LAST:event_btn_getCapacidadeActionPerformed

    private void btn_getFilaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_getFilaActionPerformed
        try {
            // Verificando se a Fila está vazia
            if(fila.isEmpty()) {
                throw new IllegalArgumentException();
            }
            String text = "[";
            // Realizando for para pegar todos os valores da fila
            for(int i = 0; i < fila.size(); i++) {
                text += fila.getPosition(i);
                if(i + 1 != fila.size()) text += ", ";
            }
            text += "]\n";
            ta_saida.setText(ta_saida.getText() + text); // Imprimindo mensagem
        } catch (IllegalArgumentException e) {
            JOptionPane.showMessageDialog(rootPane, "A Fila está vazia.", "Alerta", JOptionPane.INFORMATION_MESSAGE);
        }
    }//GEN-LAST:event_btn_getFilaActionPerformed

